import React, { useEffect, useState } from "react";
import "./App.css";
import io from "socket.io-client";
import Chat from "./Chat";

const socket = io.connect("http://localhost:3001");

function App() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const roomParam = urlParams.get("room");
    const usernameParam = urlParams.get("username");

    if (roomParam && usernameParam) {
      setUsername(usernameParam);
      setRoom(roomParam);
      joinRoom(usernameParam, roomParam);
    }
  }, []);

  const joinRoom = (usernameToJoin, roomToJoin) => {
    if (usernameToJoin !== "" && roomToJoin !== "") {
      socket.emit("join_room", roomToJoin);
      setShowChat(true);
    }
  };

  const handleJoinRoom = () => {
    if (username !== "" && room !== "") {
      joinRoom(username, room);
    }
  };

  const shareRoomLink = () => {
    if (username !== "" && room !== "") {
      const roomLink = `${window.location.origin}/?room=${room}&username=${username}`;
      if (navigator.share) {
        navigator.share({
          title: "Join my chat room",
          text: "Join my chat room using this link:",
          url: roomLink,
        })
        .then(() => console.log("Successful share"))
        .catch((error) => console.log("Error sharing", error));
      } else {
        // Fallback for browsers that do not support the Web Share API
        alert(`Copy this link to join the room: ${roomLink}`);
      }
    } else {
      alert("Please enter both username and room ID");
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Join Chat</h3>
          <input
            type="text"
            placeholder="John..."
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
          <button onClick={handleJoinRoom}>Join A Room</button>
          <button onClick={shareRoomLink}>Share Room Link</button>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} />
      )}
    </div>
  );
}

export default App;
