import React, { useState } from 'react';
import io from 'socket.io-client';
import Chat from './Chat';
import { v4 as uuidv4 } from 'uuid';

const socket = io.connect("http://localhost:3001"); // Change this to your server address

function App() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [password, setPassword] = useState("");
  const [showChat, setShowChat] = useState(false);

  const createRoom = () => {
    if (username !== "" && password !== "") {
      const roomId = uuidv4();
      setRoom(roomId);
      socket.emit("create_room", { room: roomId, password });
      setShowChat(true);
    }
  };

  const joinRoom = () => {
    if (username !== "" && room !== "" && password !== "") {
      socket.emit("join_room", { room, password }, (response) => {
        if (response.status === "ok") {
          setShowChat(true);
        } else {
          alert("Incorrect room ID or password");
        }
      });
    }
  };

  const handleShare = () => {
    const shareUrl = `${window.location.origin}/?room=${room}&password=${password}`;
    navigator.clipboard.writeText(shareUrl);
    alert("Room link copied to clipboard");
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Create or Join a Room</h3>
          <input
            type="text"
            placeholder="Username..."
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          {!room && (
            <>
              <input
                type="password"
                placeholder="Password..."
                onChange={(event) => {
                  setPassword(event.target.value);
                }}
              />
              <button onClick={createRoom}>Create Room</button>
            </>
          )}
          {room && (
            <>
              <input
                type="text"
                placeholder="Room ID..."
                value={room}
                onChange={(event) => {
                  setRoom(event.target.value);
                }}
              />
              <input
                type="password"
                placeholder="Password..."
                onChange={(event) => {
                  setPassword(event.target.value);
                }}
              />
              <button onClick={joinRoom}>Join Room</button>
              <button onClick={handleShare}>Share Room</button>
            </>
          )}
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} />
      )}
    </div>
  );
}


export default App;
